<?phpclass ControllerModuleCategoryHome extends Controller {    public function index($setting) {        $data['heading_title'] = $this->language->get('heading_title');        $this->load->model('news/category');        $this->load->model('news/news');        $this->load->model('tool/image');        $data['category'] = array();        if (!empty($setting['category_id'])) {            $category = array_slice($setting['category_id'], 0, 4);            foreach ($category as $category_id) {                $category_info = $this->model_news_category->getCategory($category_id);                                if ($category_info) {                    if ($category_info['image']) {                        $image = $this->model_tool_image->resize($category_info['image'], '48', '48');                    } else {                        $image = $this->model_tool_image->resize('placeholder.png', '48', '48');                    }                    $data_['newss'] = array();                    $filter_data = array(                        'filter_category_id' => $category_info['category_id'],                        'filter_filter' => '',                        'sort' => '',                        'order' => '',                        'start' => 0,                        'limit' => 10                    );                    $results = $this->model_news_news->getNewss($filter_data);                                        foreach ($results as $result) {                        $data_['newss'][] = array(                            'news_id' => $result['news_id'],                            'thumb' => $image,                            'name' => $result['name'],                            'date_added' => date($this->language->get('date_format_short'), strtotime($result['date_added'])),                            'description' => utf8_substr(strip_tags(html_entity_decode($result['description'], ENT_QUOTES, 'UTF-8')), 0, $this->config->get($this->config->get('config_theme') . '_news_description_length')) . '..',                            'rating' => $result['rating'],                            'href' => $this->url->link('news/news', 'cat_news_id=' . $this->request->get['cat_news_id'] . '&news_id=' . $result['news_id'] . $url)                        );                    }                    $data['category'][$category_info['category_id']] = array(                        'meta_description' => utf8_substr(strip_tags(html_entity_decode($category_info['meta_description'], ENT_QUOTES, 'UTF-8')), 0, 149) . '...',                        'description' => html_entity_decode($category_info['description']),                        'image' => $image,                        'category_id' => $category_info['category_id'],                        'name' => $category_info['name'],                        'href' => $this->url->link('news/category/info?categpry_id=' . $category_info['category_id']),                        'news' => $data_['newss']                    );                }            }        }        if ($data['category']) {            return $this->load->view('module/category_home', $data);        }    }}