<?phpclass ControllerModuleCategoryNews extends Controller {    public function index($setting) {        $data['heading_title'] = $this->language->get('heading_title');        $this->load->model('news/category');        $this->load->model('news/news');        $this->load->model('tool/image');        $data['name'] = '';        if (!empty($setting['name'])) {            $data['name'] = $setting['name'];        }        $data['category_left'] = array();        if (!empty($setting['category_left_id'])) {            $category = array_slice($setting['category_left_id'], 0, 8);            foreach ($category as $category_id) {                $category_info = $this->model_news_category->getCategory($category_id);                if ($category_info) {                    if ($category_info['image']) {                        $image = $this->model_tool_image->resize($category_info['image'], '48', '48');                    } else {                        $image = $this->model_tool_image->resize('placeholder.png', '48', '48');                    }                    $data_['newss'] = array();                    $filter_data = array(                        'filter_category_id' => $category_info['category_id'],                        'filter_filter' => '',                        'sort' => '',                        'order' => '',                        'start' => 0,                        'limit' => 4                    );                    $results = $this->model_news_news->getNewss($filter_data);                    foreach ($results as $result) {                        if ($result['image']) {                            $image = $this->model_tool_image->resize($result['image'], '121', '71', 'resize_and_crop');                        } else {                            $image = $this->model_tool_image->resize('placeholder.png', '121', '71', 'resize_and_crop');                        }                        $data_['newss'][] = array(                            'news_id' => $result['news_id'],                            'thumb' => $image,                            'meta_description' => substr($result['meta_description'], 0, 140) . '...',                            'name' => $result['name'],                            'date_added' => date($this->language->get('date_format_short'), strtotime($result['date_added'])),                            'description' => utf8_substr(strip_tags(html_entity_decode($result['description'], ENT_QUOTES, 'UTF-8')), 0, $this->config->get($this->config->get('config_theme') . '_news_description_length')) . '..',                            'rating' => $result['rating'],                            'href' => $this->url->link('news/news', 'cat_news_id=' . $this->request->get['cat_news_id'] . '&news_id=' . $result['news_id'] . $url)                        );                    }                    $data['category_left'][$category_info['category_id']] = array(                        'description' => html_entity_decode($category_info['description']),                        'image' => $image,                        'category_id' => $category_info['category_id'],                        'name' => $category_info['name'],                        'href' => $this->url->link('news/category/info?categpry_id=' . $category_info['category_id']),                        'news' => $data_['newss']                    );                }            }        }        $data['category_right'] = array();        if (!empty($setting['category_right_id'])) {            $category = $setting['category_right_id'];            $category_info = $this->model_news_category->getCategory($category);            //var_dump($category_info);die;            if ($category_info) {                $data_['newss'] = array();                $filter_data = array(                    'filter_category_id' => $category_info['category_id'],                    'filter_filter' => '',                    'sort' => '',                    'order' => '',                    'start' => 0,                    'limit' => 4                );                $results = $this->model_news_news->getNewss($filter_data);                foreach ($results as $result) {                    if ($result['image']) {                        $image = $this->model_tool_image->resize($result['image'], '121', '71', 'resize_and_crop');                    } else {                        $image = $this->model_tool_image->resize('placeholder.png', '121', '71', 'resize_and_crop');                    }                    $data_['newss'][] = array(                        'news_id' => $result['news_id'],                        'name' => $result['name'],                        'meta_description' => $result['meta_description'],                        'image' => $image,                        'rating' => $result['rating'],                        'href' => $this->url->link('news/news', 'cat_news_id=' . $this->request->get['cat_news_id'] . '&news_id=' . $result['news_id'] . $url)                    );                }                $data['category_right'] = array(                    'name' => $category_info['name'],                    'news' => $data_['newss']                );            }        }                $data['news_news'] = array();        $filter_data = array(            'filter_category_id' => '',            'filter_filter' => '',            'sort' => 'p.news_id',            'order' => 'DESC',            'start' => 0,            'limit' => 9        );        $results = $this->model_news_news->getNewss($filter_data, '2');        foreach ($results as $result) {            if ($result['image']) {                $image = $this->model_tool_image->resize($result['image'], '121', '71', 'resize_and_crop');            } else {                $image = $this->model_tool_image->resize('placeholder.png', '121', '71', 'resize_and_crop');            }            $data['news_news'][] = array(                'news_id' => $result['news_id'],                'name' => $result['name'],                'meta_description' => $result['meta_description'],                'image' => $image,                'rating' => $result['rating'],                'href' => $this->url->link('news/news', 'cat_news_id=' . $this->request->get['cat_news_id'] . '&news_id=' . $result['news_id'] . $url)            );        }        $data['sum_view'] = 0;        $sum_view = $this->model_news_news->getSumNew();        if($sum_view){            $data['sum_view']  = $sum_view[0]['total'];        }        $data['news_total'] = array();        $filter_data = array(            'filter_category_id' => '',            'filter_filter' => '',            'sort' => 'p.viewed',            'order' => 'DESC',            'start' => 0,            'limit' => 8        );        $results = $this->model_news_news->getNewss($filter_data, '2');        foreach ($results as $result) {            if ($result['image']) {                $image = $this->model_tool_image->resize($result['image'], '121', '71', 'resize_and_crop');            } else {                $image = $this->model_tool_image->resize('placeholder.png', '121', '71', 'resize_and_crop');            }            $data['news_total'][] = array(                'news_id' => $result['news_id'],                'name' => $result['name'],                'meta_description' => $result['meta_description'],                'image' => $image,                'rating' => $result['rating'],                'href' => $this->url->link('news/news', 'cat_news_id=' . $this->request->get['cat_news_id'] . '&news_id=' . $result['news_id'] . $url)            );        }        if ($data) {            return $this->load->view('module/category_news', $data);        }    }}