<?phpclass ControllerModuleUserHome extends Controller {    public function index($setting) {        $data['heading_title'] = $this->language->get('heading_title');        $this->load->model('user/user');                $this->load->model('tool/image');        if (is_file(DIR_IMAGE . $this->config->get('config_logo'))) {            $data['logo'] = $server . 'image/' . $this->config->get('config_logo');        } else {            $data['logo'] = '';        }        $data['name'] = '';        $data['text_introduce'] = '';        $data['user_info_1'] = array();        $data['user_info_2'] = array();        $data['user_info_3'] = array();                if (!empty($setting['name'])) {            $data['name'] = $setting['name'];        }        if (!empty($setting['text_introduce'])) {            $data['text_introduce'] = $setting['text_introduce'];        }        if (!empty($setting['user_id_1'])) {            $data['user_info_1'] = $this->model_user_user->getUser($setting['user_id_1']);            if ($data['user_info_1']['image']) {                $data['user_info_1']['image'] = $this->model_tool_image->resize($data['user_info_1']['image'], '220', '220');            } else {                $data['user_info_1']['image'] = $this->model_tool_image->resize('placeholder.png', '220', '220');            }                    }        if (!empty($setting['user_id_2'])) {            $data['user_info_2'] = $this->model_user_user->getUser($setting['user_id_2']);            if ($data['user_info_2']['image']) {                $$data['user_info_2']['image'] = $this->model_tool_image->resize($data['user_info_2']['image'], '220', '220');            } else {                $$data['user_info_2']['image'] = $this->model_tool_image->resize('placeholder.png', '220', '220');            }                    }        if (!empty($setting['user_id_3'])) {            $data['user_info_3'] = $this->model_user_user->getUser($setting['user_id_3']);            if ($data['user_info_3']['image']) {                $$data['user_info_3']['image'] = $this->model_tool_image->resize($data['user_info_3']['image'], '220', '220');            } else {                $$data['user_info_3']['image'] = $this->model_tool_image->resize('placeholder.png', '220', '220');            }                    }        if ($data) {            return $this->load->view('module/user_home', $data);        }    }    private function getPath($menu_id, $menu_group_id) {        $this->load->model('menu/menu');        $menus = array();        $results = $this->model_menu_menu->getMenus($menu_id, $menu_group_id);        foreach ($results as $result) {            $get_name = $this->model_menu_menu->getNameMenu($result['menu_id'], $result['menu_type_id'], $result['name_id']);            $menus[] = array(                'menu_id' => $result['menu_id'],                'name' => $get_name['name'],                'children' => $this->getPath($result['menu_id'], $menu_group_id),                'href' => $get_name['href'],                'attribute' => $get_name['attribute']            );        }        return $menus;    }}